(()=>{"use strict";const e=new class{constructor(e){this._element=e}createBoard(){let e=document.createElement("div");e.classList.add("board");for(let t=0;t<16;t+=1){let l=document.createElement("div");l.classList.add("cell"),l.dataset.index=t+1,e.appendChild(l)}this._element.appendChild(e)}createGoblin(){let e=Array.from(this._element.querySelector(".board").children),t=0,l=0;this._element.querySelector(".cell_selected")?(l=Math.floor(Math.random()*e.length),t!==l||(l=Math.floor(Math.random()*e.length)),e.forEach((e=>e.classList.remove("cell_selected")))):(l=Math.floor(Math.random()*e.length),t=l),e[l].classList.add("cell_selected")}}(document.querySelector(".board-container"));document.addEventListener("DOMContentLoaded",(()=>{e.createBoard(),setInterval((()=>e.createGoblin()),2e3)}))})();